/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react"

const Dropper = ({ color, x }) => {
  const style = css`
    margin-top: 2rem;
    background-color: #d3d3d3;
    text-align: center;
    & p {
      background-color: ${color};
    }
    & ul {
      list-style: none;
    }
  `

  return (
    <div css={style}>
      <p>
        {color} x{x}
      </p>
      <ul>
        <li></li>
      </ul>
    </div>
  )
}

export default Dropper
