/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react"

const Roulette = () => {
  const styles = {
    rouletteContainer: css`
      width: 100%;
      display: flex;
    `,
    box: css`
      width: 80px;
      height: 80px;
    `,
    redBox: css`
      background-color: red;
    `,
    blackBox: css`
      background-color: black;
    `,
  }

  let rouletteAmount = 34
  let rouletteBoxes = []

  for (let i = 0; i < rouletteAmount; i++) {
    rouletteBoxes = [
      ...rouletteBoxes,
      <div key={i} css={[styles.box, styles.redBox]}></div>,
    ]
    if (i % 2 == 0) {
      document.write("Number is even!")
    } else {
      document.write("Number is odd!")
    }
  }

  return <div css={styles.rouletteContainer}>{rouletteBoxes}</div>
}

export default Roulette
