/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react"
import { useEffect, useState } from "react"

const Roulette = () => {
  let rouletteAmount = 136
  let rouletteBoxes = []

  // Timer with cleanup
  useEffect(() => {
    const timer = setInterval(() => {
      setTimer((prevState) => {
        console.log(prevState)
        return prevState - 1
      })
    }, 1000)
    // clearing interval
    return () => clearInterval(timer)
  })

  const styles = {
    rouletteWrapper: css`
      width: 100%;
      overflow: hidden;
      position: relative;
      &::after {
        content: "";
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        width: 5px;
        height: 100%;
        background-color: blue;
        opacity: 80%;
      }
    `,
    info: css`
      display: flex;
      justify-content: space-between;
      font-size: 25px;
      margin-bottom: 0.5rem;
    `,

    rouletteBoxesContainer: css`
      display: flex;
      transition: all 10s;
      transform: translateX(0);
    `,
    box: css`
      min-width: 100px;
      min-height: 100px;
    `,
    redBox: css`
      background-color: red;
    `,
    blackBox: css`
      background-color: black;
    `,
    greenBox: css`
      background-color: green;
    `,
  }

  console.log(rouletteAmount * 100)

  const [timer, setTimer] = useState(10)

  // Pushing boxes to array
  function pushBox(color, key) {
    rouletteBoxes = [
      ...rouletteBoxes,
      <div key={key} css={[styles.box, styles[color]]}></div>,
    ]
  }

  // Generating boxes
  for (let i = 0; i < rouletteAmount; i++) {
    if (i === 10) {
      // Number even
      pushBox("greenBox", i)
      i++
    } else if (i % 2 === 0) {
      pushBox("redBox", i)
    } else {
      // Number odd
      pushBox("blackBox", i)
    }
  }

  return (
    <div css={styles.rouletteWrapper}>
      <div css={styles.info}>
        <p>Balance = 100DKK</p>
        <p>Spinning in {timer} seconds</p>
      </div>
      <div css={styles.rouletteBoxesContainer}>{rouletteBoxes}</div>
    </div>
  )
}

export default Roulette
