/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react"

const Roulette = () => {
  const styles = {
    rouletteContainer: css`
      width: 100%;
      display: flex;
    `,
    box: css`
      min-width: 80px;
      min-height: 80px;
    `,
    redBox: css`
      background-color: red;
    `,
    blackBox: css`
      background-color: black;
    `,
    greenBox: css`
      background-color: green;
    `,
  }

  let rouletteAmount = 68
  let rouletteBoxes = []

  function pushBox(color, key) {
    rouletteBoxes = [
      ...rouletteBoxes,
      <div key={key} css={[styles.box, styles[color]]}></div>,
    ]
  }

  for (let i = 0; i < rouletteAmount; i++) {
    if (i === 10) {
      // Number even
      pushBox("greenBox", i)
      i++
    } else if (i % 2 === 0) {
      pushBox("redBox", i)
    } else {
      // Number odd
      pushBox("blackBox", i)
    }
  }

  return <div css={styles.rouletteContainer}>{rouletteBoxes}</div>
}

export default Roulette
