/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react"

const Roulette = () => {
  const styles = {
    rouletteContainer: css`
      width: 100%;
      display: flex;
    `,
    box: css`
      min-width: 80px;
      min-height: 80px;
    `,
    redBox: css`
      background-color: red;
    `,
    blackBox: css`
      background-color: black;
    `,
    greenBox: css`
      background-color: green;
    `,
  }

  let rouletteAmount = 68
  let rouletteBoxes = []

  for (let i = 0; i < rouletteAmount; i++) {
    if (i === 10) {
      // Number even
      rouletteBoxes = [
        ...rouletteBoxes,
        <div key={i} css={[styles.box, styles.redBox]}></div>,
      ]
    } else if (i % 2 === 0) {
      rouletteBoxes = [
        ...rouletteBoxes,
        <div key={i} css={[styles.box, styles.greenBox]}></div>,
      ]
    } else {
      // Number odd
      rouletteBoxes = [
        ...rouletteBoxes,
        <div key={i} css={[styles.box, styles.blackBox]}></div>,
      ]
    }
  }

  return <div css={styles.rouletteContainer}>{rouletteBoxes}</div>
}

export default Roulette
