/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react"

const Roulette = () => {
  const styles = {
    rouletteWrapper: css`
      width: 100%;
      display: flex;
      overflow: hidden;
      transition: all 10s;
    `,
    box: css`
      min-width: 100px;
      min-height: 100px;
    `,
    redBox: css`
      background-color: red;
    `,
    blackBox: css`
      background-color: black;
    `,
    greenBox: css`
      background-color: green;
    `,
  }

  let rouletteAmount = 68
  let rouletteBoxes = []

  // Pushing boxes to array
  function pushBox(color, key) {
    rouletteBoxes = [
      ...rouletteBoxes,
      <div key={key} css={[styles.box, styles[color]]}></div>,
    ]
  }

  // Generating boxes
  for (let i = 0; i < rouletteAmount; i++) {
    if (i === 10) {
      // Number even
      pushBox("greenBox", i)
      i++
    } else if (i % 2 === 0) {
      pushBox("redBox", i)
    } else {
      // Number odd
      pushBox("blackBox", i)
    }
  }

  return (
    <div css={styles.rouletteWrapper}>
      <div css={styles.rouletteBoxesContainer}>{rouletteBoxes}</div>
    </div>
  )
}

export default Roulette
