/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react"

const Dropper = ({ color, x, users }) => {
  const style = css`
    background-color: #d3d3d3;
    text-align: center;
    cursor: pointer;
    min-height: 400px;
    & > p {
      background-color: ${color};
      color: white;
      padding: 1rem;
    }
    & ul {
      list-style: none;
      & li {
        display: flex;
        align-items: center;
        justify-content: space-between;
        margin: 0 1rem;
        & img {
          width: 50px;
        }
        & > div {
          display: flex;
          align-items: center;
        }
      }
    }
  `

  return (
    <div css={style}>
      <p>
        {color} x{x}
      </p>
      <ul>
        {users &&
          users.map((user) => (
            <li>
              <div>
                <img
                  src="https://avatars.dicebear.com/api/adventurer/benjamin.svg"
                  alt=""
                />
                <p>benjamin</p>
              </div>
              <p>100dkk</p>
            </li>
          ))}
      </ul>
    </div>
  )
}

export default Dropper
